// src/components/Checkout/CheckoutPage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  MapPin, 
  Phone, 
  Mail, 
  User, 
  Home, 
  CreditCard,
  DollarSign,
  ShoppingBag,
  Store,
  AlertCircle,
  Check,
  ChevronDown,
  ChevronUp,
  X
} from 'lucide-react';
import { useCart } from '../../context/CartContext';
import woocommerceApi from '../../services/woocommerceApi';
import './CheckoutPage.css';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const { cart, getCartTotal, clearCart } = useCart();

  // Estados principales
  const [currentStep, setCurrentStep] = useState(1);
  const [orderType, setOrderType] = useState('delivery');
  const [loading, setLoading] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);
  const [orderId, setOrderId] = useState(null);
  const [shippingInfo, setShippingInfo] = useState(null);
  const [paymentMethods, setPaymentMethods] = useState([]);
  const [loadingShipping, setLoadingShipping] = useState(false);

  // Estado del formulario
  const [formData, setFormData] = useState({
    customerName: '',
    email: '',
    phone: '',
    address: '',
    neighborhood: '',
    reference: '',
    paymentMethod: 'cash',
    cashAmount: '',
    notes: ''
  });

  // Estado de validación
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});

  // Estados adicionales
  const [showOrderSummary, setShowOrderSummary] = useState(window.innerWidth > 768);
  const [showAddressSuggestions, setShowAddressSuggestions] = useState(false);
  const [savedAddresses, setSavedAddresses] = useState([]);

  // Cargar métodos de pago al montar el componente
  useEffect(() => {
    loadPaymentMethods();
  }, []);

  // Cargar direcciones guardadas
  useEffect(() => {
    const addresses = JSON.parse(localStorage.getItem('savedAddresses') || '[]');
    setSavedAddresses(addresses);
  }, []);

  // Calcular envío cuando cambie la dirección o el tipo de pedido
  useEffect(() => {
    if (orderType === 'delivery' && formData.address && formData.neighborhood) {
      calculateShipping();
    }
  }, [orderType, formData.address, formData.neighborhood]);

  // Cargar métodos de pago desde WooCommerce
  const loadPaymentMethods = async () => {
    try {
      const methods = await woocommerceApi.getPaymentMethods();
      setPaymentMethods(methods.filter(m => m.enabled));
    } catch (error) {
      console.error('Error cargando métodos de pago:', error);
      // Usar métodos por defecto si falla
      setPaymentMethods([
        {
          id: 'cash',
          title: 'Efectivo',
          description: 'Paga al recibir tu pedido',
          enabled: true
        }
      ]);
    }
  };

  // Calcular costo de envío
  const calculateShipping = async () => {
    setLoadingShipping(true);
    try {
      const shippingData = await woocommerceApi.calculateShipping(
        {
          address: formData.address,
          neighborhood: formData.neighborhood
        },
        cart
      );
      setShippingInfo(shippingData);
    } catch (error) {
      console.error('Error calculando envío:', error);
      // Usar valor por defecto si falla
      setShippingInfo({
        method_id: 'flat_rate',
        method_title: 'Envío a domicilio',
        cost: 2990
      });
    } finally {
      setLoadingShipping(false);
    }
  };

  // Validación del formulario
  const validateStep = (step) => {
    const newErrors = {};

    if (step >= 1) {
      if (!formData.customerName.trim()) {
        newErrors.customerName = 'El nombre es requerido';
      }
      if (!formData.phone.trim()) {
        newErrors.phone = 'El teléfono es requerido';
      } else if (!/^(\+56)?[9][0-9]{8}$/.test(formData.phone.replace(/\s/g, ''))) {
        newErrors.phone = 'Ingresa un número válido de Chile';
      }
      if (!formData.email.trim()) {
        newErrors.email = 'El email es requerido';
      } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
        newErrors.email = 'Ingresa un email válido';
      }
    }

    if (step >= 2 && orderType === 'delivery') {
      if (!formData.address.trim()) {
        newErrors.address = 'La dirección es requerida';
      }
      if (!formData.neighborhood.trim()) {
        newErrors.neighborhood = 'La comuna es requerida';
      }
    }

    if (step >= 3) {
      if (!formData.paymentMethod) {
        newErrors.paymentMethod = 'Selecciona un método de pago';
      }
      if (formData.paymentMethod === 'cash' && formData.cashAmount) {
        const cashAmount = parseFloat(formData.cashAmount);
        const total = getCartTotal() + (shippingInfo?.cost || 0);
        if (cashAmount < total) {
          newErrors.cashAmount = 'El monto debe ser mayor o igual al total';
        }
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Manejadores de eventos
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Limpiar error al escribir
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }

    // Mostrar sugerencias de dirección
    if (name === 'address' && value.length > 3) {
      setShowAddressSuggestions(true);
    }
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched(prev => ({ ...prev, [name]: true }));
  };

  const handleNextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => Math.min(prev + 1, 3));
      window.scrollTo(0, 0);
    } else {
      // Marcar todos los campos del paso actual como touched
      const fieldsToTouch = currentStep === 1 
        ? ['customerName', 'phone', 'email']
        : currentStep === 2 && orderType === 'delivery'
        ? ['address', 'neighborhood']
        : [];

      const newTouched = fieldsToTouch.reduce((acc, field) => {
        acc[field] = true;
        return acc;
      }, {});

      setTouched(prev => ({ ...prev, ...newTouched }));
    }
  };

  const handlePrevStep = () => {
    setCurrentStep(prev => Math.max(prev - 1, 1));
    window.scrollTo(0, 0);
  };

  const selectSavedAddress = (address) => {
    setFormData(prev => ({
      ...prev,
      address: address.address,
      neighborhood: address.neighborhood,
      reference: address.reference || ''
    }));
    setShowAddressSuggestions(false);
  };

  const handleSubmitOrder = async () => {
    if (!validateStep(3)) return;

    setLoading(true);

    try {
      const orderData = {
        customerName: formData.customerName,
        email: formData.email,
        phone: formData.phone,
        orderType,
        items: cart,
        total: getCartTotal() + (orderType === 'delivery' ? (shippingInfo?.cost || 0) : 0),
        address: orderType === 'delivery' ? formData.address : null,
        neighborhood: orderType === 'delivery' ? formData.neighborhood : null,
        reference: orderType === 'delivery' ? formData.reference : null,
        paymentMethod: formData.paymentMethod,
        cashAmount: formData.paymentMethod === 'cash' ? formData.cashAmount : null,
        shipping: orderType === 'delivery' ? shippingInfo : null,
        notes: formData.notes
      };

      const order = await woocommerceApi.createOrder(orderData);

      setOrderId(order.id);
      setOrderSuccess(true);
      clearCart();

      // Guardar dirección si es nueva
      if (orderType === 'delivery' && formData.address) {
        const newAddress = {
          address: formData.address,
          neighborhood: formData.neighborhood,
          reference: formData.reference
        };

        const existingAddresses = JSON.parse(localStorage.getItem('savedAddresses') || '[]');
        const isDuplicate = existingAddresses.some(addr => 
          addr.address === newAddress.address && 
          addr.neighborhood === newAddress.neighborhood
        );

        if (!isDuplicate) {
          existingAddresses.push(newAddress);
          localStorage.setItem('savedAddresses', JSON.stringify(existingAddresses));
        }
      }

    } catch (error) {
      console.error('Error al crear pedido:', error);
      alert('Error al procesar el pedido. Por favor intenta nuevamente.');
    } finally {
      setLoading(false);
    }
  };

  const subtotal = getCartTotal();
  const deliveryFee = orderType === 'delivery' ? (shippingInfo?.cost || 0) : 0;
  const total = subtotal + deliveryFee;

  if (orderSuccess) {
    return (
      <div className="checkout-page">
        <div className="checkout-success-container">
          <div className="checkout-success-icon">
            <Check size={48} />
          </div>
          <h1 className="checkout-success-title">¡Gracias por tu pedido!</h1>
          <p className="checkout-success-order">Pedido #{orderId}</p>
          <p className="checkout-success-message">
            {orderType === 'delivery' 
              ? 'Tu pedido será entregado en 30-45 minutos'
              : 'Tu pedido estará listo para retirar en 20-30 minutos'
            }
          </p>
          <div className="checkout-success-details">
            <p>Te hemos enviado un email con los detalles de tu pedido.</p>
            <p>También recibirás un WhatsApp con la confirmación.</p>
          </div>
          <button 
            className="btn btn-primary"
            onClick={() => navigate('/')}
          >
            Volver al inicio
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="checkout-page">
      <div className="checkout-container">
        {/* Header */}
        <div className="checkout-header">
          <button 
            className="checkout-back-btn"
            onClick={() => currentStep > 1 ? handlePrevStep() : navigate('/cart')}
            aria-label="Volver"
          >
            <ArrowLeft size={20} />
            <span>Volver</span>
          </button>
          <h1 className="checkout-title">Finalizar Pedido</h1>
        </div>

        {/* Progress Bar */}
        <div className="checkout-progress">
          <div className="checkout-progress-bar">
            <div 
              className="checkout-progress-fill"
              style={{ width: `${(currentStep / 3) * 100}%` }}
            />
          </div>
          <div className="checkout-steps">
            {['Datos', orderType === 'delivery' ? 'Dirección' : 'Retiro', 'Pago'].map((step, index) => (
              <div 
                key={step}
                className={`checkout-step ${currentStep > index + 1 ? 'completed' : ''} ${currentStep === index + 1 ? 'active' : ''}`}
              >
                <div className="checkout-step-circle">
                  {currentStep > index + 1 ? (
                    <Check size={16} />
                  ) : (
                    <span>{index + 1}</span>
                  )}
                </div>
                <span className="checkout-step-label">{step}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="checkout-content">
          {/* Main Content */}
          <div className="checkout-main">
            {/* Tipo de pedido */}
            {currentStep === 1 && (
              <div className="checkout-order-type">
                <h2 className="checkout-section-title">¿Cómo quieres recibir tu pedido?</h2>
                <div className="checkout-type-options">
                  <label className={`checkout-type-option ${orderType === 'delivery' ? 'active' : ''}`}>
                    <input
                      type="radio"
                      name="orderType"
                      value="delivery"
                      checked={orderType === 'delivery'}
                      onChange={(e) => setOrderType(e.target.value)}
                    />
                    <div className="checkout-type-content">
                      <Home size={24} />
                      <h3>Delivery</h3>
                      <p>Recibe en tu domicilio</p>
                    </div>
                  </label>

                  <label className={`checkout-type-option ${orderType === 'pickup' ? 'active' : ''}`}>
                    <input
                      type="radio"
                      name="orderType"
                      value="pickup"
                      checked={orderType === 'pickup'}
                      onChange={(e) => setOrderType(e.target.value)}
                    />
                    <div className="checkout-type-content">
                      <Store size={24} />
                      <h3>Retiro en local</h3>
                      <p>Retira tu pedido</p>
                    </div>
                  </label>
                </div>
              </div>
            )}

            {/* Step 1: Datos personales */}
            {currentStep === 1 && (
              <div className="checkout-form-section">
                <h2 className="checkout-section-title">Tus datos</h2>

                <div className="checkout-form-group">
                  <label className="checkout-form-label">
                    <User size={18} />
                    Nombre completo
                  </label>
                  <input
                    type="text"
                    name="customerName"
                    value={formData.customerName}
                    onChange={handleInputChange}
                    onBlur={handleBlur}
                    className={`checkout-form-input ${errors.customerName && touched.customerName ? 'error' : ''}`}
                    placeholder="Juan Pérez"
                  />
                  {errors.customerName && touched.customerName && (
                    <span className="checkout-form-error">{errors.customerName}</span>
                  )}
                </div>

                <div className="checkout-form-group">
                  <label className="checkout-form-label">
                    <Phone size={18} />
                    Teléfono
                  </label>
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    onBlur={handleBlur}
                    className={`checkout-form-input ${errors.phone && touched.phone ? 'error' : ''}`}
                    placeholder="+56912345678"
                  />
                  {errors.phone && touched.phone && (
                    <span className="checkout-form-error">{errors.phone}</span>
                  )}
                  <span className="checkout-form-hint">Te contactaremos por WhatsApp</span>
                </div>

                <div className="checkout-form-group">
                  <label className="checkout-form-label">
                    <Mail size={18} />
                    Email
                  </label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    onBlur={handleBlur}
                    className={`checkout-form-input ${errors.email && touched.email ? 'error' : ''}`}
                    placeholder="juan@ejemplo.com"
                  />
                  {errors.email && touched.email && (
                    <span className="checkout-form-error">{errors.email}</span>
                  )}
                </div>
              </div>
            )}

            {/* Step 2: Dirección o Información de Retiro */}
            {currentStep === 2 && (
              <div className="checkout-form-section">
                <h2 className="checkout-section-title">
                  {orderType === 'delivery' ? 'Dirección de Entrega' : 'Información de Retiro'}
                </h2>

                {orderType === 'delivery' ? (
                  <>
                    <div className="checkout-form-group">
                      <label className="checkout-form-label">
                        <MapPin size={18} />
                        Dirección
                      </label>
                      <div className="checkout-input-wrapper">
                        <input
                          type="text"
                          name="address"
                          value={formData.address}
                          onChange={handleInputChange}
                          onBlur={handleBlur}
                          onFocus={() => savedAddresses.length > 0 && setShowAddressSuggestions(true)}
                          className={`checkout-form-input ${errors.address && touched.address ? 'error' : ''}`}
                          placeholder="Calle, número"
                        />
                        {showAddressSuggestions && savedAddresses.length > 0 && (
                          <div className="checkout-address-suggestions">
                            <div className="suggestions-header">
                              <span>Direcciones guardadas</span>
                              <button 
                                type="button"
                                onClick={() => setShowAddressSuggestions(false)}
                              >
                                <X size={16} />
                              </button>
                            </div>
                            {savedAddresses.map((addr, index) => (
                              <button
                                key={index}
                                type="button"
                                className="suggestion-item"
                                onClick={() => selectSavedAddress(addr)}
                              >
                                <MapPin size={16} />
                                <div>
                                  <p>{addr.address}</p>
                                  <span>{addr.neighborhood}</span>
                                </div>
                              </button>
                            ))}
                          </div>
                        )}
                      </div>
                      {errors.address && touched.address && (
                        <span className="checkout-form-error">{errors.address}</span>
                      )}
                    </div>

                    <div className="checkout-form-group">
                      <label className="checkout-form-label">
                        Comuna
                      </label>
                      <input
                        type="text"
                        name="neighborhood"
                        value={formData.neighborhood}
                        onChange={handleInputChange}
                        onBlur={handleBlur}
                        className={`checkout-form-input ${errors.neighborhood && touched.neighborhood ? 'error' : ''}`}
                        placeholder="Ej: Providencia"
                      />
                      {errors.neighborhood && touched.neighborhood && (
                        <span className="checkout-form-error">{errors.neighborhood}</span>
                      )}
                    </div>

                    <div className="checkout-form-group">
                      <label className="checkout-form-label">
                        Referencia (opcional)
                      </label>
                      <textarea
                        name="reference"
                        value={formData.reference}
                        onChange={handleInputChange}
                        className="checkout-form-textarea"
                        placeholder="Ej: Casa con reja negra, depto 301"
                        rows="2"
                      />
                    </div>

                    {loadingShipping && (
                      <div className="checkout-shipping-loading">
                        <p>Calculando costo de envío...</p>
                      </div>
                    )}

                    {shippingInfo && !loadingShipping && (
                      <div className="checkout-shipping-info">
                        <AlertCircle size={16} />
                        <p>Costo de envío: ${shippingInfo.cost.toLocaleString('es-CL')}</p>
                      </div>
                    )}
                  </>
                ) : (
                  <div className="checkout-pickup-info">
                    <Store size={48} className="checkout-pickup-icon" />
                    <h3>Retira tu pedido en nuestro local</h3>
                    <div className="checkout-pickup-details">
                      <p><strong>Dirección:</strong> Camino las parcelas 12, Isla de Maipo</p>
                      <p><strong>Horario:</strong></p>
                      <p>Lunes a jueves: 18:00 - 22:00 hrs</p>
                      <p>Viernes: 18:00 - 23:00 hrs</p>
                      <p>Sábado: 13:00 - 23:00 hrs</p>
                      <p>Domingo: 13:00 - 22:00 hrs</p>
                      <p><strong>Teléfono:</strong> +56 942740261</p>
                    </div>
                    <div className="checkout-pickup-note">
                      <AlertCircle size={16} />
                      <p>Te enviaremos un WhatsApp cuando tu pedido esté listo</p>
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* Step 3: Pago */}
            {currentStep === 3 && (
              <div className="checkout-form-section">
                <h2 className="checkout-section-title">Método de Pago</h2>

                <div className="checkout-payment-options">
                  {paymentMethods.map(method => (
                    <label key={method.id} className="checkout-payment-option">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value={method.id}
                        checked={formData.paymentMethod === method.id}
                        onChange={handleInputChange}
                      />
                      <div className="checkout-payment-content">
                        <DollarSign size={24} />
                        <div>
                          <h3>{method.title}</h3>
                          <p>{method.description}</p>
                        </div>
                      </div>
                    </label>
                  ))}

                  {formData.paymentMethod === 'cash' && (
                    <div className="checkout-cash-amount">
                      <label className="checkout-form-label">
                        ¿Con cuánto pagarás? (opcional)
                      </label>
                      <input
                        type="number"
                        name="cashAmount"
                        value={formData.cashAmount}
                        onChange={handleInputChange}
                        onBlur={handleBlur}
                        className={`checkout-form-input ${errors.cashAmount ? 'error' : ''}`}
                        placeholder={`Mínimo $${total.toLocaleString('es-CL')}`}
                        min={total}
                      />
                      {errors.cashAmount && (
                        <span className="checkout-form-error">{errors.cashAmount}</span>
                      )}
                    </div>
                  )}
                </div>

                {/* Notas adicionales */}
                <div className="checkout-form-group">
                  <label className="checkout-form-label">
                    Notas adicionales (opcional)
                  </label>
                  <textarea
                    name="notes"
                    value={formData.notes}
                    onChange={handleInputChange}
                    className="checkout-form-textarea"
                    placeholder="¿Alguna instrucción especial para tu pedido?"
                    rows="3"
                  />
                </div>
              </div>
            )}

            {/* Botones de navegación */}
            <div className="checkout-navigation">
              {currentStep > 1 && (
                <button 
                  className="btn btn-secondary"
                  onClick={handlePrevStep}
                >
                  Anterior
                </button>
              )}

              {currentStep < 3 ? (
                <button 
                  className="btn btn-primary"
                  onClick={handleNextStep}
                >
                  Siguiente
                </button>
              ) : (
                <button 
                  className="btn btn-primary"
                  onClick={handleSubmitOrder}
                  disabled={loading}
                >
                  {loading ? 'Procesando...' : 'Confirmar Pedido'}
                </button>
              )}
            </div>
          </div>

          {/* Sidebar - Resumen del pedido */}
          <div className="checkout-sidebar">
            <div className="checkout-order-summary">
              <button 
                className="checkout-summary-toggle"
                onClick={() => setShowOrderSummary(!showOrderSummary)}
              >
                <h3>Resumen del pedido</h3>
                {showOrderSummary ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
              </button>

              {showOrderSummary && (
                <div className="checkout-summary-content">
                  <div className="checkout-summary-items">
                    {cart.map((item, index) => (
                      <div key={index} className="checkout-summary-item">
                        <div>
                          <p>{item.quantity}x {item.name}</p>
                          {item.variations && (
                            <span className="item-variations">{item.variations}</span>
                          )}
                        </div>
                        <span>${(item.price * item.quantity).toLocaleString('es-CL')}</span>
                      </div>
                    ))}
                  </div>

                  <div className="checkout-summary-totals">
                    <div className="checkout-summary-row">
                      <span>Subtotal</span>
                      <span>${subtotal.toLocaleString('es-CL')}</span>
                    </div>
                    {orderType === 'delivery' && (
                      <div className="checkout-summary-row">
                        <span>Envío</span>
                        <span>
                          {loadingShipping ? 'Calculando...' : `$${deliveryFee.toLocaleString('es-CL')}`}
                        </span>
                      </div>
                    )}
                    <div className="checkout-summary-total">
                      <span>Total</span>
                      <span>${total.toLocaleString('es-CL')}</span>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CheckoutPage;
