// src/components/CustomizationModal/CustomizationModal.jsx
import React, { useState, useContext, useEffect } from 'react';
import { X, Plus, Minus, Info, Check, Loader, Pizza, Upload } from 'lucide-react';
import { CartContext } from '../../context/CartContext';
import woocommerceApi from '../../services/woocommerceApi';
import './CustomizationModal.css';

const CustomizationModal = ({ product, isOpen, onClose }) => {
  const { addToCart } = useContext(CartContext);

  // Estados para datos de WooCommerce
  const [variations, setVariations] = useState([]);
  const [attributes, setAttributes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Estados para Advanced Product Fields
  const [productAddons, setProductAddons] = useState([]);
  const [addonValues, setAddonValues] = useState({});
  const [addonFiles, setAddonFiles] = useState({});

  // Estados de selección
  const [selectedAttributes, setSelectedAttributes] = useState({});
  const [selectedVariation, setSelectedVariation] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [specialInstructions, setSpecialInstructions] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);
  const [addingToCart, setAddingToCart] = useState(false);

  // Reset estados cuando se abre el modal
  useEffect(() => {
    if (isOpen && product) {
      resetForm();
      loadProductData();
    }
  }, [isOpen, product]);

  // Actualizar precio cuando cambia la selección
  useEffect(() => {
    updatePrice();
  }, [selectedVariation, quantity, product, addonValues]);

  const resetForm = () => {
    setVariations([]);
    setAttributes([]);
    setSelectedAttributes({});
    setSelectedVariation(null);
    setQuantity(1);
    setSpecialInstructions('');
    setProductAddons([]);
    setAddonValues({});
    setAddonFiles({});
    setError(null);
  };

  const loadProductData = async () => {
    setLoading(true);
    setError(null);

    try {
      // Si es un producto variable, cargar variaciones
      if (product.type === 'variable') {
        const variationsData = await woocommerceApi.getProductVariations(product.id);
        setVariations(variationsData || []);

        if (product.attributes) {
          const variableAttributes = product.attributes.filter(attr => attr.variation);
          setAttributes(variableAttributes);

          const defaultAttributes = {};
          variableAttributes.forEach(attr => {
            if (attr.options && attr.options.length > 0) {
              defaultAttributes[attr.name] = attr.options[0];
            }
          });
          setSelectedAttributes(defaultAttributes);
          findMatchingVariation(defaultAttributes, variationsData);
        }
      } else {
        setTotalPrice(parseFloat(product.price) * quantity);
      }

      // Cargar Advanced Product Fields
      await loadProductAddons(); // <-- Asegurarse de que espere

    } catch (err) {
      console.error('Error loading product data:', err);
      setError('Error al cargar las opciones del producto. Por favor, intenta de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  // =======================================================================
  // FUNCIÓN CORREGIDA
  // =======================================================================
  const loadProductAddons = async () => {
    console.log('=== BUSCANDO ADDONS ===');

    try {
      // 1. Intenta obtener los campos directamente desde tu API personalizada
      console.log(`Intentando obtener campos para producto ID: ${product.id} via API...`);
      const response = await fetch(`/wp-json/custom/v1/product-fields/${product.id}`);

      if (response.ok) {
        const data = await response.json();
        console.log('Campos obtenidos via API personalizada:', data);

        // El endpoint devuelve un objeto { fields: [...] }, así que accedemos a 'fields'
        if (data && data.fields && data.fields.length > 0) {
          setProductAddons(data.fields);
          initializeAddonValues(data.fields);
          // ¡Éxito! Salimos de la función aquí.
          return; 
        }
      } else {
         console.log(`API personalizada respondió con estado: ${response.status}`);
      }

      // 2. Si la API falla o no devuelve campos, intenta los métodos de respaldo
      console.log('La API no devolvió campos, buscando en meta_data...');
      if (product.meta_data) {
        const possibleFieldKeys = ['_wapf_fields', '_product_addons', 'wapf_fields'];
        for (const key of possibleFieldKeys) {
          const fieldsData = product.meta_data.find(meta => meta.key === key);
          if (fieldsData && fieldsData.value) {
            console.log(`Campos encontrados con key "${key}":`, fieldsData.value);
            const fields = Array.isArray(fieldsData.value) ? fieldsData.value : [fieldsData.value];
            setProductAddons(fields);
            initializeAddonValues(fields);
            return;
          }
        }
      }

      console.log('No se encontraron addons para este producto.');

    } catch (error) {
      console.error('Error cargando los addons del producto:', error);
      // No establecer un error general aquí para no sobreescribir otros errores
    }
  };

  const initializeAddonValues = (fields) => {
    const defaultValues = {};
    fields.forEach(field => {
      // Usar 'id' como identificador primario, ya que parece ser el que usa el plugin
      const fieldId = field.id;
      if (!fieldId) return;

      if (field.type === 'checkbox') {
        defaultValues[fieldId] = [];
      } else if (field.type === 'number' && field.default_value !== undefined) {
        defaultValues[fieldId] = field.default_value;
      } else if (field.type === 'true_false') {
        defaultValues[fieldId] = field.default_value === '1';
      } else {
        defaultValues[fieldId] = field.default_value || '';
      }
    });
    setAddonValues(defaultValues);
  };

  const handleAttributeChange = (attributeName, value) => {
    const newAttributes = {
      ...selectedAttributes,
      [attributeName]: value
    };
    setSelectedAttributes(newAttributes);
    findMatchingVariation(newAttributes, variations);
  };

  const findMatchingVariation = (attributes, availableVariations) => {
    if (!availableVariations || availableVariations.length === 0) return;

    const matching = availableVariations.find(variation => {
      return variation.attributes.every(attr => {
        return attributes[attr.name] === attr.option;
      });
    });

    setSelectedVariation(matching || null);
  };

  const handleAddonChange = (fieldId, value) => {
    setAddonValues(prev => ({
      ...prev,
      [fieldId]: value
    }));
  };

  const handleFileUpload = (fieldId, file) => {
    if (file) {
      setAddonFiles(prev => ({
        ...prev,
        [fieldId]: file
      }));
      setAddonValues(prev => ({
        ...prev,
        [fieldId]: file.name
      }));
    }
  };

  const calculateAddonPrice = () => {
    let addonTotal = 0;

    productAddons.forEach(addon => {
      const fieldId = addon.id;
      const value = addonValues[fieldId];

      if (!value || (Array.isArray(value) && value.length === 0)) return;

      // Para campos con opciones (radio, select, checkbox)
      if (addon.options && Array.isArray(addon.options)) {
        addon.options.forEach(option => {
          const isSelected = Array.isArray(value) 
            ? value.includes(option.value)
            : value === option.value;

          if (isSelected && option.price) {
            const price = parseFloat(option.price);
            if (option.price_type === 'quantity_based') {
              addonTotal += price * quantity;
            } else {
              addonTotal += price;
            }
          }
        });
      }
      // Para campos simples con precio
      else if (addon.price) {
        const price = parseFloat(addon.price);
        if (addon.price_type === 'quantity_based') {
            addonTotal += price * quantity;
        } else if (addon.price_type === 'per_character' && typeof value === 'string') {
            addonTotal += price * value.length;
        } else if (addon.price_type === 'flat_fee' && value) {
            // para true_false o campos que se activan
             addonTotal += price;
        }
      }
    });

    return addonTotal;
  };

  const updatePrice = () => {
    let basePrice = 0;

    if (selectedVariation && selectedVariation.price) {
      basePrice = parseFloat(selectedVariation.price);
    } else if (product && product.price) {
      basePrice = parseFloat(product.price);
    }

    const addonPrice = calculateAddonPrice();
    setTotalPrice((basePrice + addonPrice) * quantity);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP'
    }).format(Math.round(price));
  };

  const isValidSelection = () => {
    if (product.type === 'variable' && (!selectedVariation || selectedVariation.stock_status === 'outofstock')) {
      setError('Por favor, selecciona una variación válida.');
      return false;
    }

    for (const addon of productAddons) {
      if (addon.required) {
        const fieldId = addon.id;
        const value = addonValues[fieldId];

        if (!value || (Array.isArray(value) && value.length === 0)) {
          setError(`Por favor, completa el campo: ${addon.label}`);
          return false;
        }
      }
    }
    setError(null);
    return true;
  };

  const handleAddToCart = async () => {
    if (!isValidSelection()) return;

    setAddingToCart(true);

    try {
      const addonMetaData = [];
      productAddons.forEach(addon => {
        const fieldId = addon.id;
        const value = addonValues[fieldId];

        if (value && !(Array.isArray(value) && value.length === 0)) {
          let displayValue = value;
          if (Array.isArray(value)) displayValue = value.join(', ');

          addonMetaData.push({
            key: addon.label,
            value: displayValue
          });
        }
      });

      const cartItem = {
        id: selectedVariation ? `${product.id}-${selectedVariation.id}-${Date.now()}` : `${product.id}-${Date.now()}`,
        productId: product.id,
        variationId: selectedVariation?.id,
        name: product.name,
        price: totalPrice / quantity,
        image: selectedVariation?.image?.src || product.images?.[0]?.src || '',
        quantity,
        customizations: {
          attributes: selectedAttributes,
          specialInstructions: specialInstructions.trim(),
          productAddons: addonMetaData,
          // Guardar los valores crudos por si los necesitas en el carrito
          addonValues: addonValues, 
        }
      };

      addToCart(cartItem);
      onClose();

    } catch (err) {
      setError('Error al agregar al carrito. Por favor, intenta de nuevo.');
    } finally {
      setAddingToCart(false);
    }
  };

  const renderAddonField = (addon) => {
    const fieldId = addon.id;
    const value = addonValues[fieldId];

    switch (addon.type) {
      case 'text':
        return (
          <input
            type="text"
            value={value || ''}
            onChange={(e) => handleAddonChange(fieldId, e.target.value)}
            placeholder={addon.placeholder || ''}
            className="addon-text-input"
            required={addon.required}
          />
        );

      case 'textarea':
        return (
          <textarea
            value={value || ''}
            onChange={(e) => handleAddonChange(fieldId, e.target.value)}
            placeholder={addon.placeholder || ''}
            rows={4}
            className="addon-textarea"
            required={addon.required}
          />
        );

      case 'number':
        return (
          <input
            type="number"
            value={value || ''}
            onChange={(e) => handleAddonChange(fieldId, e.target.value ? parseFloat(e.target.value) : '')}
            min={addon.min}
            max={addon.max}
            step={addon.step || 1}
            className="addon-number-input"
            required={addon.required}
          />
        );

      case 'select':
        return (
          <select
            value={value || ''}
            onChange={(e) => handleAddonChange(fieldId, e.target.value)}
            className="addon-select"
            required={addon.required}
          >
            <option value="">{addon.placeholder || 'Selecciona una opción'}</option>
            {addon.options?.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
                {option.price && ` (+${formatPrice(option.price)})`}
              </option>
            ))}
          </select>
        );

      case 'radio':
        return (
          <div className="addon-radio-group">
            {addon.options?.map((option) => (
              <label key={option.value} className="addon-radio-option">
                <input
                  type="radio"
                  name={fieldId}
                  value={option.value}
                  checked={value === option.value}
                  onChange={(e) => handleAddonChange(fieldId, e.target.value)}
                  required={addon.required}
                />
                <span className="radio-label">
                  {option.label}
                  {option.price && ` (+${formatPrice(option.price)})`}
                </span>
              </label>
            ))}
          </div>
        );

      case 'checkbox':
        return (
          <div className="addon-checkbox-group">
            {addon.options?.map((option) => (
              <label key={option.value} className="addon-checkbox-option">
                <input
                  type="checkbox"
                  value={option.value}
                  checked={value?.includes(option.value)}
                  onChange={(e) => {
                    const optionValue = option.value;
                    const currentValues = Array.isArray(value) ? [...value] : [];

                    if (e.target.checked) {
                      currentValues.push(optionValue);
                    } else {
                      const index = currentValues.indexOf(optionValue);
                      if (index > -1) {
                        currentValues.splice(index, 1);
                      }
                    }
                    handleAddonChange(fieldId, currentValues);
                  }}
                />
                <span className="checkbox-label">
                  {option.label}
                  {option.price && ` (+${formatPrice(option.price)})`}
                </span>
              </label>
            ))}
          </div>
        );

      default:
        console.warn('Tipo de addon no soportado:', addon.type);
        return <p>Campo "{addon.label}" no soportado.</p>;
    }
  };

  if (!isOpen || !product) return null;

  return (
    <div className="customization-modal-overlay" onClick={onClose}>
      <div className="customization-modal" onClick={(e) => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>
          <X size={24} />
        </button>

        <h2>Personaliza tu {product.name}</h2>

        {loading ? (
          <div className="loading-container">
            <Loader className="loading-spinner" size={48} />
            <p>Cargando opciones...</p>
          </div>
        ) : (
          <>
            <div className="customization-product-info">
              <img 
                src={product.images?.[0]?.src || ''} 
                alt={product.name} 
                className="customization-product-image"
              />
              <div className="product-details">
                <h3>{product.name}</h3>
                <p className="product-description" dangerouslySetInnerHTML={{ __html: product.short_description }} />
              </div>
            </div>

            {attributes.length > 0 && (
              <div className="customization-section">
                {attributes.map(attribute => (
                  <div key={attribute.id} className="attribute-section">
                    <h4>{attribute.name}</h4>
                    <div className="attribute-options">
                      {attribute.options.map(option => (
                        <label key={option} className="attribute-option">
                          <input
                            type="radio"
                            name={attribute.name}
                            value={option}
                            checked={selectedAttributes[attribute.name] === option}
                            onChange={() => handleAttributeChange(attribute.name, option)}
                          />
                          <span className="option-label">{option}</span>
                        </label>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Advanced Product Fields renderizados desde la API */}
            {productAddons.length > 0 && (
              <div className="customization-section">
                {productAddons.map((addon) => (
                  <div key={addon.id} className="addon-field">
                    <label className="addon-label">
                      {addon.label}
                      {addon.required && <span className="required">*</span>}
                    </label>
                    {addon.description && (
                      <p className="addon-description">{addon.description}</p>
                    )}
                    {renderAddonField(addon)}
                  </div>
                ))}
              </div>
            )}

            <div className="customization-section">
              <h4>Instrucciones especiales (opcional)</h4>
              <textarea
                className="special-instructions"
                value={specialInstructions}
                onChange={(e) => setSpecialInstructions(e.target.value)}
                placeholder="Ej: Sin cebolla, extra queso, etc."
                maxLength={200}
              />
            </div>

            {error && <p className="error-message">{error}</p>}

            <div className="customization-footer">
              <div className="quantity-selector">
                <button onClick={() => setQuantity(Math.max(1, quantity - 1))}><Minus size={16} /></button>
                <span>{quantity}</span>
                <button onClick={() => setQuantity(quantity + 1)}><Plus size={16} /></button>
              </div>
              <button 
                className="add-to-cart-btn"
                onClick={handleAddToCart}
                disabled={addingToCart || (product.type === 'variable' && !selectedVariation)}
              >
                {addingToCart ? 'Agregando...' : `Agregar ${formatPrice(totalPrice)}`}
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default CustomizationModal;
