// src/components/ProductModal/ProductModal.jsx
import React, { useState, useContext } from 'react';
import { X, Plus, Minus, ShoppingCart } from 'lucide-react';
import { CartContext } from '../../context/CartContext';
import './ProductModal.css';

const ProductModal = ({ product, isOpen, onClose }) => {
  const { addToCart } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  const handleAddToCart = () => {
    addToCart({
      id: product.id,
      name: product.name,
      price: parseFloat(product.price),
      image: product.images?.[0]?.src || null,
      quantity
    });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="product-modal-overlay" onClick={onClose}>
      <div className="product-modal" onClick={(e) => e.stopPropagation()}>
        <button className="product-modal-close" onClick={onClose}>
          <X size={24} />
        </button>

        <div className="product-modal-content">
          {product.images?.[0]?.src ? (
            <img 
              src={product.images[0].src} 
              alt={product.name}
              className="product-modal-image"
            />
          ) : (
            <div className="product-modal-placeholder">
              <ShoppingCart size={64} />
            </div>
          )}

          <div className="product-modal-info">
            <h2 className="product-modal-title">{product.name}</h2>

            {product.description && (
              <div 
                className="product-modal-description"
                dangerouslySetInnerHTML={{ __html: product.description }}
              />
            )}

            <div className="product-modal-price">
              {product.on_sale && product.regular_price && (
                <span className="price-regular">{formatPrice(product.regular_price)}</span>
              )}
              <span className="price-sale">{formatPrice(product.price)}</span>
            </div>

            <div className="product-modal-actions">
              <div className="quantity-selector">
                <button 
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  className="quantity-btn"
                >
                  <Minus size={20} />
                </button>
                <span className="quantity-value">{quantity}</span>
                <button 
                  onClick={() => setQuantity(quantity + 1)}
                  className="quantity-btn"
                >
                  <Plus size={20} />
                </button>
              </div>

              <button 
                className="btn btn-primary"
                onClick={handleAddToCart}
              >
                <ShoppingCart size={20} />
                Agregar al carrito
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductModal;
